export default {
  data: () => ({
    fab: false,
    hidden: false,
    tabs: null
  }),
  computed: {
    activeFab() {
      switch (this.tabs) {
        case 'one':
          return {
            color: 'success',
            icon: 'mdi-share-variant'
          };
        case 'two':
          return {
            color: 'red',
            icon: 'mdi-pencil'
          };
        case 'three':
          return {
            color: 'green',
            icon: 'mdi-chevron-up'
          };
        default:
          return {};
      }
    }
  }
};
